def getLocale(resource) {
    def path = resource.getPath();
    if(path.startsWith("/content/experience-fragments")) {
        path =path.replace("/experience-fragments", "")
        def regex = "^((?:[^/]*\\/){4}[^/]*)";
        def pattern = Pattern.compile(regex);
        def matcher = pattern.matcher(path);
        path = matcher.find() ? matcher.group(1) : path
    }
    path = path.replaceAll('/jcr:content.*', "")
    def page = getPage(path)
    if (page == null){
        println """Page is not found: ${path}"""
        return new Locale("en");
    }
    return page.getLanguage();
}

def translate_SHOWCASE_PANEL(phrase, locale) {
    def localeString = locale.toString();
    def language = locale.getLanguage();

    if(DICTIONARY_SHOWCASE_PANEL.containsKey(localeString) && DICTIONARY_SHOWCASE_PANEL[localeString].containsKey(phrase)) {
        return DICTIONARY_SHOWCASE_PANEL[localeString][phrase];
    } else if (DICTIONARY_SHOWCASE_PANEL.containsKey(language) && DICTIONARY_SHOWCASE_PANEL[language].containsKey(phrase)) {
        return DICTIONARY_SHOWCASE_PANEL[language][phrase];
    } else {
        return phrase;
    }
}

